mapper.select:
SELECT BOARD_ID , TITLE , BOARD_TEXT , DATE_FORMAT(JOY_DATE, '%Y-%m-%d') AS JOY_DATE , DATE_FORMAT(CREATED_DATE, '%Y-%m-%d %T') AS CREATED_DATE , DATE_FORMAT(MODIFIED_DATE, '%Y-%m-%d %T') AS MODIFIED_DATE , B.PUB_YN , B.DEL_YN , B.MEMBER_ID , M.USER_ID , J.JOY_ID , J.JOY_NAME , R.COLOR FROM board B LEFT OUTER JOIN member M ON B.MEMBER_ID = M.MEMBER_ID LEFT OUTER JOIN joy J ON B.JOY_ID = J.JOY_ID LEFT OUTER JOIN MEMBER_JOY_R R ON R.JOY_ID = J.JOY_ID
AND B.MEMBER_ID = #{member_id}
AND B.DEL_YN = #{del_yn}
ORDER BY BOARD_ID DESC

SELECT B.BOARD_ID , TITLE , BOARD_TEXT , DATE_FORMAT(JOY_DATE, '%Y-%m-%d') AS JOY_DATE , DATE_FORMAT(CREATED_DATE, '%Y-%m-%d %T') AS CREATED_DATE , DATE_FORMAT(MODIFIED_DATE, '%Y-%m-%d %T') AS MODIFIED_DATE , B.PUB_YN , B.DEL_YN , B.MEMBER_ID , M.USER_ID , J.JOY_ID , J.JOY_NAME , IFNULL(R.FILE_ID, 0) AS FILE_ID FROM BOARD B LEFT OUTER JOIN MEMBER M ON B.MEMBER_ID = M.MEMBER_ID LEFT OUTER JOIN JOY J ON B.JOY_ID = J.JOY_ID LEFT OUTER JOIN BOARD_FILE_R R ON B.BOARD_ID = R.BOARD_ID
B.BOARD_ID = #{board_id}
LIMIT 1

mapper.insert:
INSERT INTO BOARD
(BOARD_ID
, TITLE
, BOARD_TEXT
, JOY_DATE
, CREATED_DATE
, DEL_YN
, MEMBER_ID
, PUB_YN

   , JOY_ID


   , FILE_ID

)
VALUES
(
NEXT VALUE FOR [dbo].[board_seq]
, #{title}
, #{board_text}
, #{joy_date}
, GETDATE()
, 'N'
, #{member_id}
, #{pub_yn}

    , #{joy_id}


    , #{file_id}

)


    SELECT current_value FROM sys.sequences WHERE name ='board_seq'

mapper.update:
UPDATE BOARD
DEL_YN = 'Y', MODIFIED_DATE = GETDATE()
WHERE BOARD_ID = #{board_id}
