mapper.insert:
INSERT INTO JOY
(JOY_ID
, JOY_NAME
, DETAIL
, DEL_YN
)
VALUES
(
#{joy_id}
, #{joy_name}
, #{detail}
, 'N'
)

mapper.select:
WITH RECURSIVE MONTHS AS ( SELECT DATE_FORMAT(CURDATE(), '%Y-01-01') AS MONTH UNION ALL SELECT DATE_ADD(month, INTERVAL 1 MONTH) FROM MONTHS WHERE MONTH < DATE_FORMAT(CURDATE(), '%Y-12-31') ) SELECT DATE_FORMAT(M.MONTH, '%Y-%m') AS MONTH, COUNT(B.JOY_DATE) AS COUNT FROM MONTHS M LEFT JOIN BOARD B ON DATE_FORMAT(B.JOY_DATE, '%Y-%m') = DATE_FORMAT(M.MONTH, '%Y-%m')
AND B.MEMBER_ID = #{member_id}
WHERE YEAR(M.MONTH) = YEAR(CURDATE()) GROUP BY M.MONTH ORDER BY M.MONTH
